import { Redirect } from "react-router";
import {
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  SIGNIN_REQUEST,
  SIGNIN_SUCCESS,
} from "../actionTypes";

const initialState = {
  user: {}
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SIGNUP_SUCCESS:
      Redirect()      
    case SIGNIN_SUCCESS:
      return {
        ...state,
        user: action.userData,
      };
    default:
      return state;
  }
};
