import config from './config';


export function get (searchQuery) {
    fetch(`${config.API_URL}/movies?title=${searchQuery}`)
    .then(res => { return res.json(); })
    .then(movies => {
        console.log(movies);
        return movies
    });
}

export async function getById (id) {
    return await fetch(`${config.API_URL}/movies/${id}`);
}
