import { Redirect } from "react-router-dom";
import {
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  SIGNIN_REQUEST,
  SIGNIN_SUCCESS,
} from "../actionTypes";

const initialState = {
  user: local,
  error: {}
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SIGNUP_SUCCESS:
      if (action) {
          console.log(action);
          //<Redirect to="/sign-in"/>
      }     
    case SIGNIN_SUCCESS:
      return {
        ...state,
        user: action.userData,
      };
    default:
      return state;
  }
};
