import { takeEvery } from "redux-saga/effects";
import { getMovies } from "./moviesSaga";
import {GET_MOVIES_REQUEST} from '../reducers/moviesReducer';;

export function* rootSaga () {
    yield takeEvery(GET_MOVIES_REQUEST, getMovies);
    yield takeEvery(GET_MOVIE_REQUEST, getMovies);

}
