import { call, put } from "redux-saga/effects";
import { getMoviesSuccess } from "../reducers/moviesReducer";
import { get } from "../services/movieService";

function getMoviesRequest() {
  return getmovies()
  .then(res => {return res.json()})
}

export function* getMovies(action) {
  try {
    const response = yield call(getMoviesRequest);
    const { data } = response;
    console.log(response);
    yield put(getMoviesSuccess(data));
  } catch (error) {
    console.log(error);
  }
}
