import { Redirect } from "react-router-dom";
import {
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  SIGNIN_REQUEST,
  SIGNIN_SUCCESS,
} from "../actionTypes";

const initialState = {
  user: localStorage.getItem('username'),
  error: {}
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SIGNUP_SUCCESS:
      if (action) {
          console.log(action);
          //<Redirect to="/sign-in"/>
      }     
    case SIGNIN_SUCCESS:
   if(action.userData){
       state.user = localStorage.getItem('username')
   }

    default:
      return state;
  }
};
