import {
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  SIGNIN_REQUEST,
  SIGNIN_SUCCESS,
} from "../actionTypes";

const initialState = {
  user: {}
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SIGNUP_SUCCESS:
      const { userData } = action;
      return {
        ...state,
        user: user,
      };      
    case SIGNIN_SUCCESS:
      const { user } = action;
      return {
        ...state,
        user,
      };
    default:
      return state;
  }
};
