import { takeEvery, put } from "redux-saga/effects";





const initialState = {
  movies: [],
  movieTitle: '',
  movieId: ''
};

export const moviesReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_MOVIES_REQUEST":
      const { movieTitle } = action;
      return {
        ...state,
        movieTitle
      }
    case "GET_MOVIES_SUCCESS":
      const { movies } = action;
      return {
        ...state,
        movies
      }
      case "GET_MOVIE_REQUEST":
        const { movieId } = action;
        return {
          ...state,
          movieId
        }
      case "GET_MOVIE_SUCCESS":
        const { movie } = action;
        return {
          ...state,
          movie
        }
      default: 
        return state;
    }
};


export const movieReducer = (state = {}, action) => {
  switch (action.type) {
      case "GET_MOVIE_REQUEST":
        const { movieId } = action;
        return {
          ...state,
          movieId
        }
      case "GET_MOVIE_SUCCESS":
        const { movie } = action;
        return {
          ...state,
          movie
        }
      default: 
        return state;
    }
};
