import { useContext, useEffect, useState } from "react";
import Parser from 'html-react-parser';

import StarRatings from "react-star-ratings";

import { Link } from "@material-ui/core";
import Comments from "./Comments/Comments";

import { getById } from "../../services/movieService";
import * as favoriteService from "../../services/favoriteService";
import * as reviewService from '../../services/reviewService';

import { useDispatch, useSelector} from 'react-redux';

import AuthContext from "../../contexts/AuthContext";
import { getMovieRequest } from "../../reducers/moviesReducer";

export default function Details({match}) {
  const username = useContext(AuthContext);

  const [isFavorite, setIsFavorite] = useState(false);
  const [rating, setRating] = useState(0);
  const [isUserRating, setIsUserRating] = useState(false);

  const dispatch = useDispatch();

  const movieId = match.params.id;

  useEffect(() => {
    dispatch(getMovieRequest(movieId));

  }, [movieId]);

  const movie = useSelector((state) => state.movie);
  console.log(movie);

  useEffect(() => {



  }, [rating]); 

  useEffect(() => {

    if(username) {
      favoriteService.get()
      .then(res => { return res.json(); })
      .then((favorites) => {
        if(favorites.length > 0) {
          const isFavorite = favorites.some(x => x.movieId == movie.id);
          if(isFavorite) {
              setIsFavorite(true);
          } 
        }
      })
      .catch(error => console.log(error));
    }

  }, [movie]);

  const addFavorite = (e) => {
    e.preventDefault();
    const addMovie = Object.assign(
        {},
        {
          movieId: movie.id,
          name: movie.name,
          genres: movie.genres,
          runtime: movie.runtime,
          officialSite: movie.officialSite,
          summary: movie.summary,
          imageUrl: movie.imageUrl,
        }
      );
      favoriteService.add(addMovie)
    .then(res => {
        if(res.ok) {
            setIsFavorite(true);
        }
    })
    .catch(error => console.log(error));
  };

  const removeFavorite = (e) => {
    e.preventDefault();

    favoriteService.remove(movie.id)
    .then(res => {
        if(res.ok) {
            setIsFavorite(false);
        }
    })
    .catch(error => console.log(error));
  };



  const changeRating = async (newRating, name) => {


    try {
      await reviewService.addRatings({
        rating: newRating,
        username: name,
        movieId: movie.id
      });
        setIsUserRating(false);


    } catch(error) {
      console.log(error);
    }


  };

  return (
    <section>

    </section>
  );
}
